        &lt;final&gt;true&lt;/final&gt;  
   &lt;/property&gt;  
   &lt;property&gt;  
        &lt;name&gt;dfs.replication&lt;/name&gt;  
        &lt;value&gt;2&lt;/value&gt;  
&lt;/property&gt;
&lt;property&gt;  
         &lt;name&gt;dfs.permissions&lt;/name&gt;  
